import styled from "styled-components/native";
import colors from "../../styles/colors";

export const Container = styled.Text`
  color: ${colors.gray};
  background: ${colors.primary};

  font-size: 14px;
  font-family: roboto_700;
  text-transform: uppercase;

  padding: 9px 0;
`;
